import { useState } from "react";
import { Button } from "react-bootstrap";
import Form from "react-bootstrap/Form";

export const FormComponent = ({ updateResult }) => {
  const [amount, setAmount] = useState("");
  const [interest, setInterest] = useState("");
  const [years, setYears] = useState("");

  const mainFunc = (amount, interest, numberOfYears) => {
    const annualAmount = amount / numberOfYears;
    const interestPercentage = interest / 100;

    for (let i = 0; i <= numberOfYears; i += 1) {
      const res =
        annualAmount + (amount - annualAmount * i) * interestPercentage;
      return res;
    }

    // const res =
    //   amount * interestPercentage +
    //   annualAmount +
    //   (amount - annualAmount * 1) * interestPercentage +
    //   (amount - annualAmount * 2) * interestPercentage;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const result = mainFunc(amount, interest, years);
    updateResult(result);
    setAmount("");
    setInterest("");
    setYears("");
  };

  const handleName = ({ target }) => {
    switch (target.name) {
      case "amount":
        setAmount(target.value);
        break;
      case "interest":
        setInterest(target.value);
        break;
      case "years":
        setYears(target.value);
        break;
      default:
        break;
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Credit Amount</Form.Label>
        <Form.Control
          required
          // minLength={3}
          // pattern="[A-Za-z]{1,32}"
          value={amount}
          name="amount"
          type="text"
          // placeholder="Amount"
          autoFocus
          onChange={handleName}
        />
      </Form.Group>
      <Form.Group
        required
        className="mb-3"
        controlId="exampleForm.ControlInput2"
      >
        <Form.Label>Interest Value</Form.Label>
        <Form.Control
          required
          // minLength={3}
          // pattern="[A-Za-z]{1,32}"
          value={interest}
          name="interest"
          type="text"
          // placeholder="Interest value"
          onChange={handleName}
        />
      </Form.Group>
      <Form.Group
        required
        className="mb-3"
        controlId="exampleForm.ControlInput3"
      >
        <Form.Label>Years</Form.Label>
        <Form.Control
          required
          // minLength={3}
          // pattern="[A-Za-z]{1,32}"
          value={years}
          name="years"
          type="text"
          // placeholder="Interest value"
          onChange={handleName}
        />
      </Form.Group>
      <Button type="submit" variant="dark">
        Submit
      </Button>
    </Form>
  );
};
